{"filter":false,"title":"1_required.php","tooltip":"/w3schools-tutorials/16_Forms/3_Form_Required/1_required.php","undoManager":{"mark":0,"position":0,"stack":[[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":0,"column":0},"end":{"row":0,"column":16}},"text":"<!DOCTYPE HTML> "},{"action":"insertText","range":{"start":{"row":0,"column":16},"end":{"row":1,"column":0}},"text":"\n"},{"action":"insertLines","range":{"start":{"row":1,"column":0},"end":{"row":90,"column":0}},"lines":["<html>","<head>","<style>",".error {color: #FF0000;}","</style>","</head>","<body> ","","<?php","// define variables and set to empty values","$nameErr = $emailErr = $genderErr = $websiteErr = \"\";","$name = $email = $gender = $comment = $website = \"\";","","if ($_SERVER[\"REQUEST_METHOD\"] == \"POST\") {","   if (empty($_POST[\"name\"])) {","     $nameErr = \"Name is required\";","   } else {","     $name = test_input($_POST[\"name\"]);","   }","   ","   if (empty($_POST[\"email\"])) {","     $emailErr = \"Email is required\";","   } else {","     $email = test_input($_POST[\"email\"]);","   }","     ","   if (empty($_POST[\"website\"])) {","     $website = \"\";","   } else {","     $website = test_input($_POST[\"website\"]);","   }","","   if (empty($_POST[\"comment\"])) {","     $comment = \"\";","   } else {","     $comment = test_input($_POST[\"comment\"]);","   }","","   if (empty($_POST[\"gender\"])) {","     $genderErr = \"Gender is required\";","   } else {","     $gender = test_input($_POST[\"gender\"]);","   }","}","","function test_input($data) {","   $data = trim($data);","   $data = stripslashes($data);","   $data = htmlspecialchars($data);","   return $data;","}","?>","","<h2>PHP Form Validation Example</h2>","<p><span class=\"error\">* required field.</span></p>","<form method=\"post\" action=\"<?php echo htmlspecialchars($_SERVER[\"PHP_SELF\"]);?>\"> ","   Name: <input type=\"text\" name=\"name\">","   <span class=\"error\">* <?php echo $nameErr;?></span>","   <br><br>","   E-mail: <input type=\"text\" name=\"email\">","   <span class=\"error\">* <?php echo $emailErr;?></span>","   <br><br>","   Website: <input type=\"text\" name=\"website\">","   <span class=\"error\"><?php echo $websiteErr;?></span>","   <br><br>","   Comment: <textarea name=\"comment\" rows=\"5\" cols=\"40\"></textarea>","   <br><br>","   Gender:","   <input type=\"radio\" name=\"gender\" value=\"female\">Female","   <input type=\"radio\" name=\"gender\" value=\"male\">Male","   <span class=\"error\">* <?php echo $genderErr;?></span>","   <br><br>","   <input type=\"submit\" name=\"submit\" value=\"Submit\"> ","</form>","","<?php","echo \"<h2>Your Input:</h2>\";","echo $name;","echo \"<br>\";","echo $email;","echo \"<br>\";","echo $website;","echo \"<br>\";","echo $comment;","echo \"<br>\";","echo $gender;","?>","","</body>"]},{"action":"insertText","range":{"start":{"row":90,"column":0},"end":{"row":90,"column":7}},"text":"</html>"}]}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":0,"column":0},"end":{"row":90,"column":7},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1408532958316,"hash":"a665230f3f665f97b2a66a9fb7752baa20c230d3"}